<?phpclass UploadController{    /**     * Show page upload file     *     * @return bool     * @throws Exception     */    public function actionIndex()    {        if (isset($_FILES["file"])) {            $validate = Validate::fileValidator($_FILES["file"]);            if (!empty($validate)) {                $errors = $validate;            } else {                $fileName = random_int(10000, 9999999) . $_FILES["file"]["name"];                $saveFile = move_uploaded_file($_FILES['file']['tmp_name'], ROOT . '/upload/' . $fileName);                $arrayBooks = Upload::parseFile($fileName);                $saveBooks = Upload::saveBooks($arrayBooks);                die(1);                header("Location: http://" . $_SERVER['HTTP_HOST'] . "/");                exit;            }        }        require_once(ROOT . '/view/upload/index.php');        return true;    }}