<?phpclass Index{    /**     * Get hash strin by message or generate for date     *     * @param string $string     * @return bool|string     * @throws Exception     */    public static function GetHashString(string $string)    {        if (!empty($string)) {            $newHashString = base64_encode($string);        } else {            $getCurrentDate = random_int(0, 999999);            $newHashString = base64_encode($getCurrentDate);        }        return $newHashString;    }}