<?phpclass Information{    /**     * Delete stars book_id = id in table books     *     * @param $id     * @return mixed     */    public static function deleteParamInformation($id)    {        $db = Db::getConnection();        $queryDeleteStars = 'DELETE FROM information WHERE for_system = :for_system';        $delete = $db->prepare($queryDeleteStars);        $delete->bindParam(':for_system', $id, PDO::PARAM_STR);        $delete->execute();        Db::closeDbConnection($db);        return true;    }}