<?phpclass Stars{    /**     * Get all stars from Db     *     * @param null $db     * @return mixed     */    public static function getAllStars($db)    {        $stars = $db->query('SELECT * FROM stars')            ->fetchAll(PDO::FETCH_ASSOC);        return $stars;    }    /**     * Get stars where book_id = id in table books     *     * @param $db     * @param $id     * @return mixed     */    public static function getStarsByBookId($db, $id)    {        $stars = $db->query('SELECT * FROM stars Where books_id =' . $id)            ->fetchAll(PDO::FETCH_ASSOC);        return $stars;    }    /**     * Create stars for new Book     *     * @param $bookId     * @param null $stars     * @return bool     */    public static function createStars($bookId, $stars = null)    {        $db = Db::getConnection();        if (isset($bookId) && !empty($stars)) {            self::saveStar($db, $bookId, $stars);        } else {            self::saveStar($db, $bookId, $_POST);        }        Db::closeDbConnection($db);        return false;    }    /**     * Save all stars to db     *     * @param $db     * @param $bookId     * @param $actor     * @return bool     */    public static function saveStar($db, $bookId, $actor)    {        if (!empty($actor['stars_first'])) {            $newActor = [                htmlspecialchars($bookId),                htmlspecialchars($actor['stars_first']),                htmlspecialchars($actor['stars_last'])            ];        } else if ($actor[0] && $actor[1]) {            $newActor = [                htmlspecialchars($bookId),                htmlspecialchars(trim($actor[0])),                htmlspecialchars(trim($actor[1]))            ];        }        $insertQuery = "INSERT INTO stars (books_id, first_name, last_name) VALUES             ($newActor[0], '$newActor[1]', '$newActor[2]');";        $db->exec($insertQuery);        return true;    }    /**     * Delete stars book_id = id in table books     *     * @param $db     * @param $id     * @return mixed     */    public static function deleteStars($db, $id)    {        $queryDeleteStars = 'DELETE FROM stars WHERE books_id = :id';        $delete = $db->prepare($queryDeleteStars);        $delete->bindParam(':id', $id, PDO::PARAM_INT);        $delete->execute();        return true;    }}