<?phpclass Upload{    /**     * Parse file txt and create array item books     *     * @param $file     * @return array     */    public static function parseFile($file)    {        $lines = file(ROOT . '/upload/' . $file);        $resultBooks = [];        $item = [];        foreach ($lines as $line_num => $line) {            if (empty(trim($line[0]))) {                $resultBooks[] = $item;                $item = [];            } else {                $twoString = explode(":", $line, 2);                $item[] = $twoString;            }        }        return $resultBooks;    }    /**     * Save books and Actors from upload file     *     * @param $arrayBooks     * @return bool     */    public static function saveBooks($arrayBooks)    {        foreach ($arrayBooks as $item) {            $validateBook = Validate::uploadBookValidator($item);            $saveBook = ($validateBook) ? Books::createBooks($item) : false;            if ($saveBook) {                $actors = $twoString = explode(",", $item[3][1]);                foreach ($actors as $actor) {                    $actorName = explode(" ", trim($actor));                    $validateStars = Validate::uploadStarValidator($actorName[0], $actorName[1]);                    if ($validateStars) {                        Stars::createStars($saveBook, [$actorName[0], $actorName[1]]);                    }                }            }        }        return true;    }}