<?phpclass Upload{    /**     * Parse file txt and create array item books     *     * @param $file     * @return array     */    public static function parseFile($file)    {        $lines = file(ROOT . '/upload/' . $file);        $resultBooks = [];        $item = [];        foreach ($lines as $line_num => $line) {            if (empty(trim($line[0]))) {                $resultBooks[] = $item;                $item = [];            } else {                $twoString = explode(":", $line);                $item[] = $twoString;            }        }        return $resultBooks;    }    /**     * @param $arrayBooks     */    public static function saveBooks($arrayBooks)    {        foreach ($arrayBooks as $item) {            $validate =  Validate::uploadFileValidator($item);//            Books::createBooks();            echo '<pre>';            die(var_dump($validate));            echo '<pre>';        }    }}